import axios from 'axios';
import React, { useEffect, useState } from 'react';

const App = () => {
  const [fileSelected, setFileSelected] = useState(false);
  const [fileName, setFileName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [file, setFile] = useState(null);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      setUser(JSON.parse(loggedInUser));
    }
  }, []);

  const closeModal = (e) => {
    if (e.target.classList.contains('modal')) {
      setIsLoginModalOpen(false);
      setIsSignUp(false);
      setIsForgotPassword(false);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
    setFileSelected(true);
  };

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/signup', { email, password });
      alert(`Registration successful: ${response.data.token}`);
      setIsSignUp(false);
    } catch (error) {
      alert(`Registration failed: ${error.response?.data?.message || 'Unknown error'}`);
    }
  };

  const getResetToken = () => {
    const path = window.location.pathname;
    const token = path.split('/').pop();
    return token;
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/login', { email, password });
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      setUser(response.data.user);
      alert(`Logging in: ${response.data.token}`);
      setIsLoginModalOpen(false);
    } catch (error) {
      alert(`Login failed: ${error.response?.data?.message || 'Unknown error'}`);
    }
  };

  const handleForgotPassword = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/reset-password', { email });
      alert(`Reset password email sent: ${response.data.message}`);
    } catch (error) {
      alert(`Reset password failed: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  const handleResetPassword = async () => {
    const resetToken = getResetToken();
    if (!resetToken) {
      alert("Error in reset password");
      return;
    }
    if (newPassword !== confirmNewPassword) {
      alert("Passwords do not match.");
      return;
    }
	
    
    try {
      const response = axios.post(`http://localhost:3000/auth/reset-password/${resetToken}`, { newPassword });
      alert(`Password reset successful: ${response.data.message}`);
    } catch (error) {
      alert(`Password reset failed: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  const handleTranslatePdf = async () => {
    if (!user) {
      setIsLoginModalOpen(true);
      return;
    }

    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    const name = prompt("Enter an optional name for the file (leave blank for none):");
    if (name) {
      formData.append('name', name);
    }

    try {
      const response = await axios.post('http://localhost:3000/pdf/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        responseType: 'json',
      });

      const binaryString = atob(response.data.pdfBuffer);
      const len = binaryString.length;
      const bytes = new Uint8Array(len);
      for (let i = 0; i < len; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }

      const blob = new Blob([bytes], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'translated.pdf';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      alert('PDF translated and ready for download');
    } catch (error) {
      alert('Translation failed, try logging in.');
    }
  };

  const languages = [
    "English", "Spanish", "French", "German", "Italian", "Portuguese", "Dutch", "Russian", "Chinese", "Japanese", "Korean", "Turkish", "Arabic", "Hindi", "Bengali", "Polish", "Swedish", "Greek", "Hebrew", "Thai", "Vietnamese"
  ];

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <h1 className="logo">Aristo</h1>
        <p className="sidebar-item left-align">📚 <span className="sidebar-text">Translate</span></p>
        <p className="sidebar-item left-align">❓ <span className="sidebar-text">‎ FAQ</span></p>
        <p className="sidebar-item left-align">💳 <span className="sidebar-text">Billing</span></p>
      </div>
      <div className="main-content no-top-margin">
        <div className="header">
          {user ? (
            <>
              <span>Welcome, {user.name}</span>
              <button className="login-btn" onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <button className="login-btn" onClick={() => setIsLoginModalOpen(true)}>Login</button>
          )}
        </div>
        <h2 className="translate-title left-align">Instantly Translate a Document</h2>
        <div className="upload-section">
          <input type="file" id="fileInput" onChange={handleFileChange} hidden />
          <label htmlFor="fileInput" className="upload-box">
            <span className="upload-icon">☁️</span>
            <p>Drag and Drop here</p>
          </label>
          {fileSelected && <p>File selected: {fileName}</p>}
        </div>
        <div className="language-selection">
          <label>Input Language:</label>
          <select>
            <option value="auto">Auto Detect</option>
            {languages.map((lang, index) => (
              <option key={index} value={lang}>{lang}</option>
            ))}
          </select>
          <label>Output Language:</label>
          <select>
            <option value="">Select Language</option>
            {languages.map((lang, index) => (
              <option key={index} value={lang}>{lang}</option>
            ))}
          </select>
        </div>
        <div className="translate-button">
          <button onClick={handleTranslatePdf}>Translate Now!</button>
        </div>
        <div className="supported-formats">
          <p>Supported formats:</p>
          <div className="format-container">
            <span className="format pdf">PDF</span>
            <span className="format doc">DOC</span>
            <span className="format txt">TXT</span>
            <span className="format pptx">PPTX</span>
          </div>
        </div>
      </div>

      {/* Login Modal */}
      {isLoginModalOpen && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content">
            <h2>{isSignUp ? 'Sign Up' : isForgotPassword ? 'Forgot Password' : 'Login'}</h2>
            <hr />
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
            />
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
            />
            {isForgotPassword ? (
              <button className="reset-btn" onClick={handleForgotPassword}>Reset Password</button>
            ) : (
              <div className="modal-buttons">
                <button onClick={isSignUp ? handleRegister : handleLogin}>{isSignUp ? 'Sign Up' : 'Login'}</button>
                <button onClick={() => setIsLoginModalOpen(false)}>Close</button>
              </div>
            )}
            <div className="modal-footer">
              {!isSignUp && !isForgotPassword && (
                <>
                  <p onClick={() => setIsSignUp(true)}>Sign Up</p>
                  <p onClick={() => setIsForgotPassword(true)}>Forgot Password?</p>
                </>
              )}
            </div>
          </div>
        </div>
      )}

      <style>
        {`
          body { font-family: 'Poppins', sans-serif; background: #f4f4f2; margin: 0; padding: 0; }
          .app-container { display: flex; }
          .sidebar { width: 250px; background: #008b8b; color: white; padding: 20px; min-height: 100vh; text-align: center; }
          .logo { font-size: 50px; font-weight: bold; margin-bottom: 30px; text-align: center; }
          .sidebar-item { margin: 20px 0; cursor: pointer; font-size: 18px; color: white; display: flex; align-items: center; gap: 10px; }
          .sidebar-text { margin-left: 10px; }
          .left-align { text-align: left; justify-content: flex-start; }
          .main-content { flex: 1; padding: 40px; background: #fcfcfa; position: relative; }
          .no-top-margin { margin-top: 0; }
          .header { display: flex; justify-content: flex-end; align-items: center; border-bottom: 2px solid #008b8b; padding-bottom: 10px; }
          .translate-title { margin-top: 20px; }
          .login-btn { background: transparent; border: none; cursor: pointer; font-size: 16px; }
          .upload-section { text-align: center; margin: 40px 0; }
          .upload-box { display: inline-block; border: 2px dashed black; padding: 40px; cursor: pointer; font-size: 18px; }
          .upload-icon { font-size: 50px; }
          .language-selection { display: flex; justify-content: center; align-items: center; gap: 20px; margin-top: 30px; font-size: 18px; }
          select { padding: 8px; font-size: 16px; background: #ddd; border: none; }
          .translate-button { text-align: center; margin-top: 30px; }
          .translate-button button { background: #008b8b; color: white; padding: 12px 25px; border: none; cursor: pointer; font-size: 18px; border-radius: 5px; box-shadow: 5px 5px 0px #69a5a5; }
          .supported-formats { position: absolute; bottom: 20px; right: 20px; text-align: left; font-size: 16px; font-style: italic; }
          .format-container { display: flex; align-items: center; gap: 10px; }
          .format { display: flex; align-items: center; justify-content: center; width: 40px; height: 40px; border-radius: 5px; color: white; font-size: 14px; font-weight: bold; }
          .pdf { background: red; }
          .doc { background: blue; }
          .txt { background: black; }
          .pptx { background: orange; }

          /* Modal Styles */
          .modal {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-color: rgba(0, 0, 0, 0.5);
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 1000;
          }
          .modal-content {
            background-color: white;
            padding: 20px;
            border-radius: 8px;
            width: 300px;
            text-align: center;
          }
          .modal input {
            width: 100%;
            padding: 10px;
            margin: 10px 0;
            border-radius: 5px;
            border: 1px solid #ccc;
          }
          .modal-buttons {
            display: flex;
            justify-content: space-between;
            margin-top: 20px;
          }
          .modal-buttons button {
            padding: 10px 20px;
            cursor: pointer;
            font-size: 16px;
            border-radius: 5px;
            border: none;
          }
          .modal-buttons button:first-child {
            background-color: #008b8b;
            color: white;
          }
          .modal-buttons button:last-child {
            background-color: #ccc;
            color: black;
          }
          .modal-footer p {
            margin-top: 10px;
            cursor: pointer;
            color: #008b8b;
          }
          .reset-btn {
            background-color: #008b8b;
            color: white;
            padding: 10px 20px;
            border-radius: 5px;
            font-size: 16px;
            border: none;
            cursor: pointer;
          }
        `}
      </style>
    </div>
  );
};

export default App;
