import axios from 'axios';
import React, { useState } from 'react';

const App = () => {
  const [fileSelected, setFileSelected] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [resetToken, setResetToken] = useState('');
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setFileSelected(true);
  };

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/signup', { email, password });
      alert(`Registration successful: ${response.data.token}`);
    } catch (error) {
      if (error.response?.data?.message) {
        const message = error.response.data.message;
        alert(`Registration failed: ${Array.isArray(message) ? message.join(', ') : message}`);
      } else {
        alert('Registration failed');
      }
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/login', { email, password });
      alert(`Logging in: ${response.data.token}`);
    } catch (error) {
      if (error.response?.data?.message) {
        alert(`Login failed: ${Array.isArray(error.response.data.message) ? error.response.data.message.join(', ') : error.response.data.message}`);
      } else {
        alert('Login failed');
      }
    }
  };

  const handleForgotPassword = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/reset-password', { email });
      alert(`Reset password email sent: ${response.data.message}`);
    } catch (error) {
      alert(`Reset password failed: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  const handleResetPassword = async () => {
    if (!resetToken) {
      alert("Reset token is required.");
      return;
    }
    if (newPassword !== confirmNewPassword) {
      alert("Passwords do not match.");
      return;
    }

    try {
      const response = await axios.post(`http://localhost:3000/reset-password/${resetToken}`, { newPassword });
      alert(`Password reset successful: ${response.data.message}`);
    } catch (error) {
      alert(`Password reset failed: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  const handleTranslatePdf = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:3000/translate', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(`Translating PDF: ${response.data.message}`);
    } catch (error) {
      console.error('Translation failed');
    }
  };

  return (
    <div>
      <h1>Welcome to the Application</h1>

      <div>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>

      <div>
        <button onClick={handleRegister}>REGISTER</button>
        <button onClick={handleLogin}>LOGIN</button>
        <button onClick={handleForgotPassword}>FORGOT PASSWORD</button>
      </div>

      <h2>Reset Password</h2>
      <div>
        <input
          type="text"
          placeholder="Reset Token"
          value={resetToken}
          onChange={(e) => setResetToken(e.target.value)}
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="New Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Confirm New Password"
          value={confirmNewPassword}
          onChange={(e) => setConfirmNewPassword(e.target.value)}
        />
      </div>
      <div>
        <button onClick={handleResetPassword}>RESET PASSWORD</button>
      </div>

      <div>
        <input type="file" onChange={handleFileChange} />
      </div>

      {fileSelected && (
        <div>
          <button onClick={handleTranslatePdf}>TRANSLATE PDF</button>
        </div>
      )}
    </div>
  );
};

export default App;
