import axios from 'axios';
import React, { useState } from 'react';

const App = () => {
  const [fileSelected, setFileSelected] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setFileSelected(true);
  };

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/signup', { email, password });
      alert(`Registration successful: ${response.data.token}`);
    } catch (error) {
      if (error.response && error.response.data && error.response.data.message) {
        const message = error.response.data.message;
        const errorMessage = Array.isArray(message) ? message.join(', ') : message;
        alert(`Registration failed: ${errorMessage}`);
      } else {
        alert('Registration failed');
      }
    }
  };



  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/login', {
        email,
        password
      });
      alert(`Logging in: ${response.data.token}`);
    } catch (error) {
      console.error('Login failed');
      if (error.response && error.response.data && error.response.data.message) {
        const message = error.response.data.message;
        const errorMessage = Array.isArray(message) ? message.join(', ') : message;
        alert(`Login failed: ${error.response.data.message.join(', ')}`);
      } else {
        alert('Login failed');
      }
    }
  };

  const handleForgotPassword = () => {
    alert('Redirecting to forgot password page');
  };

  const handleTranslatePdf = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:3000/translate', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(`Translating PDF: ${response.data.message}`);
    } catch (error) {
      console.error('Translation failed');
    }
  };

  return (
    <div>
      <h1>Welcome to the Application</h1>

      <div>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>

      <div>
        <button onClick={handleRegister}>REGISTER</button>
        <button onClick={handleLogin}>LOGIN</button>
        <button onClick={handleForgotPassword}>FORGOT PASSWORD</button>
      </div>

      <div>
        <input type="file" onChange={handleFileChange} />
      </div>

      {fileSelected && (
        <div>
          <button onClick={handleTranslatePdf}>TRANSLATE PDF</button>
        </div>
      )}
    </div>
  );
};

export default App;
