import axios from 'axios';
import React, { useState } from 'react';

const App = () => {
  const [fileSelected, setFileSelected] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [file, setFile] = useState(null);


  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setFileSelected(true);
  };

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/signup', { email, password });
      alert(`Registration successful: ${response.data.token}`);
    } catch (error) {
      if (error.response?.data?.message) {
        const message = error.response.data.message;
        alert(`Registration failed: ${Array.isArray(message) ? message.join(', ') : message}`);
      } else {
        alert('Registration failed');
      }
    }
  };

  const getResetToken = () => {
    const path = window.location.pathname;
    const token = path.split('/').pop();  // Extracts the last part after "reset-password/"
    return token;
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/login', { email, password });
      localStorage.setItem('token', response.data.token);
      alert(`Logging in: ${response.data.token}`);
    } catch (error) {
      if (error.response?.data?.message) {
        alert(`Login failed: ${Array.isArray(error.response.data.message) ? error.response.data.message.join(', ') : error.response.data.message}`);
      } else {
        alert('Login failed');
      }
    }
  };

  const handleForgotPassword = async () => {
    try {
      const response = await axios.post('http://localhost:3000/auth/reset-password', { email });
      alert(`Reset password email sent: ${response.data.message}`);
    } catch (error) {
      alert(`Reset password failed: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  const handleResetPassword = async () => {
    const resetToken = getResetToken();
    console.log(resetToken);

    if (!resetToken) {
      alert("Error in reset password");
      return;
    }
    if (newPassword !== confirmNewPassword) {
      alert("Passwords do not match.");
      return;
    }

    if (newPassword.length < 7) {
      alert("Password must be at least 7 characters.");
      return;
    }

    try {
      const response = await axios.post(`http://localhost:3000/auth/reset-password/${resetToken}`, { newPassword });
      alert(`Password reset successful: ${response.data.message}`);
    } catch (error) {
      alert(`Password reset failed: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  const handleTranslatePdf = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    // Add the optional name if it's provided
    const name = prompt("Enter an optional name for the file (leave blank for none):");
    if (name) {
      formData.append('name', name);
    }

    try {
      const response = await axios.post('http://localhost:3000/pdf/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        responseType: 'json', // Ensure the response is treated as a binary buffer
      });

      const binaryString = atob(response.data.pdfBuffer);
      const len = binaryString.length;
      console.log(len);
      const bytes = new Uint8Array(len);
      for (let i = 0; i < len; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }
      // Create a Blob from the PDF buffer
      const blob = new Blob([bytes], { type: 'application/pdf' });

      // Create a download link
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'translated.pdf'; // Set the file name
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      alert('PDF translated and ready for download');
    } catch (error) {
      console.error('Translation failed', error);
      alert('Translation failed');
    }
  };



  return (
    <div>
      <h1>Welcome to the Application</h1>

      <div>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>

      <div>
        <button onClick={handleRegister}>REGISTER</button>
        <button onClick={handleLogin}>LOGIN</button>
        <button onClick={handleForgotPassword}>FORGOT PASSWORD</button>
      </div>

      <h2>Reset Password</h2>
      <div>
        <input
          type="password"
          placeholder="New Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Confirm New Password"
          value={confirmNewPassword}
          onChange={(e) => setConfirmNewPassword(e.target.value)}
        />
      </div>
      <div>
        <button onClick={handleResetPassword}>RESET PASSWORD</button>
      </div>

      <div>
        <input type="file" onChange={handleFileChange} />
      </div>

      {fileSelected && (
        <div>
          <button onClick={handleTranslatePdf}>TRANSLATE PDF</button>
        </div>
      )}


    </div>
  );
};

export default App;
